const Sidebar = () => {
  return (
    <aside>
      <ul className="sidebarPanel">
        <li className="header">
          <span className="menuIcon">{MenuIcon}</span>
        </li>
        <div className="options">
          <li className="mainOptions labels">{HomeIcon}Home</li>
          <li className="mainOptions labels">{MegaphoneIcon}Comunicados</li>
          <li className="mainOptions labels">{ReportIcon}Reportes</li>
          <li className="mainOptions labels">{HandsIcon}Aportes</li>
          <li className="mainOptions labels">{ProccessIcon}Processos</li>
        </div>
        <li className="footer">
          <span className="mainOptions labels">{FooterIcon}Home</span>
        </li>
      </ul>
    </aside>
  );
};

export default Sidebar;
